INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('1', 'The Fool', 'This card represents the dreamer in you, the idealist, the mystic. The Fool desires to do great things. He must always be very careful of the choices he makes, and remember knowledge is his ally.', '0th.jpg', '0.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('1r', 'The Fool Reversed', 'This is the card of folly, indescretion and poor choices.', '0r.jpg', '0.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('2', 'The Magician', 'This card represents the force of your will, the mastery of skills and your creative talents.', '1th.jpg', '1.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('2r', 'The Magician Reversed', 'This card represents the misuse of power, a weak will, indiscretion, and incompetence.', '1r.jpg', '1.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('3', 'The High Priestess', 'The High Priestess represents knowledge, truth, and an awareness of what lies beneath the surface. ', '2th.jpg', '2.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('3r', 'The High Priestess Reversed', 'The Priestess reversed represents uncontrolled sensuality, conceit and a willingness to accept the superficial.', '2r.jpg', '2.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('4', 'The Empress', 'This card represents wealth, marriage, fertility and creativity.', '3th.jpg', '3.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('4r', 'The Empress Reversed', 'This card represents infertility, instability and the loss of material possessions.', '3r.jpg', '3.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('5', 'The Emperor', 'The qualities of The Emperor include the ability to lead and wield power. He is paternal and mentally quick as well.', '4th.jpg', '4.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('5r', 'The Emperor Reversed', 'This card represents immaturity and loss of control. ', '4r.jpg', '4.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('6', 'The Hierophant', 'A lover of tradition and ceremony, The Hierphant needs social approval and appreciates the positive aspects of conformity.', '5th.jpg', '5.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('6r', 'The Hierophant Reversed', 'Open to new ideas, iconoclastic and nontraditional.', '5r.jpg', '5.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('7', 'The Lovers', 'The drawing of two forces together, choices, temptations. The fight between the sacred and the secular. Accord of the inner and worldly self.', '6th.jpg', '6.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('7r', 'The Lovers Reversed', 'Fights, betrayals and the possibility of shattered bonds. Be careful of bad choices.', '6r.jpg', '6.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('8', 'The Chariot', 'Victory and success in the world.', '7th.jpg', '7.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('8r', 'The Chariot Reversed', 'Decadence, failing health and need for change.', '7r.jpg', '7.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('9', 'Strength', 'This card represents the power of your charactor, spiritual power defeating material desires and love over hate.', '8th.jpg', '8.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('9r', 'Strength Reversed', 'This card represents defeat by the material and mundane, chaos, a lack of morals, fear and the misuse of power.', '8r.jpg', '8.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('10', 'The Hermit', 'The Hermit represents the quiet, solitary pursuit of inner knowledge. He is a seeker of deeper, truer understandings of all things.', '9th.jpg', '9.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('10r', 'The Hermit Reversed', 'The reversed Hermit hears the truth but will not listen. He is immature, callow and rife with vices.', '9r.jpg', '9.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('11', 'The Wheel of Fortune', 'This card signifies success and a change of fortune for the better. Chance is on your side.', '10th.jpg', '10.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('11r', 'The Wheel of Fortune Reversed', 'Chance is not presently favorable. Loss is possible and bravery is required.', '10r.jpg', '10.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('12', 'Justice', 'This card represents the fulfillment of justice and balance. ', '11th.jpg', '11.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('12r', 'Justice Reversed', 'The upsidedown Justice represents injustice, inequality and a lack of mercy or understanding.', '11r.jpg', '11.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('13', 'The Hanged Man', 'Spiritually wise and prophetic, the conquest of the worldly.', '12th.jpg', '12.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('13r', 'The Hanged Man Reversed', 'The card of false prophecy and time wasted. The reversed Hanged Man represents a preoccupation with the worldly and wasted energy.', '12r.jpg', '12.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('14', 'Death', 'This is the card of change and renewal, the emergence of new ideas and opportunites.', '13th.jpg', '13.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('14r', 'Death Reversed', 'This is the card of disastor, upheaval and stagnation. ', '13r.jpg', '13.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('15', 'Temperance', 'This card represents harmony, self control and synchronization with the world and others. ', '14th.jpg', '14.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('15r', 'Temperance Reversed', 'Corruption, disorganization, fighting and interests at odds with one another.', '14r.jpg', '14.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('16', 'The Devil', 'This card represents the dark forces in the world, misuse of power, depression and addiction to the material world.', '15th.jpg', '15.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('16r', 'The Devil Reversed', 'The reversed Devil signifies a spiritual rebirth, freedom from the preoccupation with the secular and the overcoming of pride and selfishness.', '15r.jpg', '15.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('17', 'The Tower', 'This card represents change, conflict and possible catastrophe.', '16th.jpg', '16.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('17r', 'The Tower Reversed', 'The attainment of physical and spiritual freedom at great cost. ', '16r.jpg', '16.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('18', 'The Star', 'This is the card of hope, perception and revelation. Great love is possible and good health will be enjoyed.', '17th.jpg', '17.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('18r', 'The Star Reversed', 'The Reversed Star represents pessimism, doubt and obstinacy. ', '17r.jpg', '17.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('19', 'The Moon', 'This card represents serendipity, creativity and the coming to life of psychic powers. It may also signify dangers yet seen, deception and bad luck for someone dear to you.', '18th.jpg', '18.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('19r', 'The Moon  Reversed', 'The containment of imagination by the practical. Peace will be known, but at a price. Risks of any kind should not be taken.', '18r.jpg', '18.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('20', 'The Sun', 'This card represents material success and the achievement of goals, fulfillment in the simple life.', '19th.jpg', '19.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('20r', 'The Sun Reversed', 'The outcome of future plans is nebulous. Trouble with relationships and work is very possible.', '19r.jpg', '19.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('21', 'Judgement', 'This card represents a fulfilling life, work done well and a clear awareness of the universe we live in.', '20th.jpg', '20.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('21r', 'Judgement Reversed', 'The card represents the failure to find happiness, disenchantment, and the loss of possessions.', '20r.jpg', '20.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('22', 'The World', 'This card represents the success in all its forms, liberation from want and a celestial consciousness.', '21th.jpg', '21.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('22r', 'The World Reversed', 'This card represents the success yet attained, a lack of clear vision and a fear of change. ', '21r.jpg', '21.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('23', 'Ace of Wands', 'A new venture is on the horizon. This new venture may be anything from a journey or business to a family.', '22th.jpg', '22.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('23r', 'Ace of Wands Reversed', 'There may be false starts ahead. New ventures of all kinds may be postponed.', '22r.jpg', '22.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('24', 'Two of Wands', 'New ventures will require courage and logic. Influence over others grows. Kindness and altruism pay off. ', '23th.jpg', '23.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('24r', 'Two of Wands Reversed', 'New ventures may prove unfruitful. Adversity may be experienced in all or any arena of life.', '23r.jpg', '23.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('25', 'Three of Wands', 'Hope, strength and world power will be realized. Arrogance is your enemy. Partnerships may bring your hopes to a good end.', '24th.jpg', '24.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('25r', 'Three of Wands Reversed', 'Be wary of offers of assistance. Underhandedness and failure are possible.', '24r.jpg', '24.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('26', 'Four of Wands', 'Work will be well done. Peace and harmony will be realized. ', '25th.jpg', '25.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('26r', 'Four of Wands Reversed', 'Peace and prospertiy will be had, yet something may still seem missing.', '25r.jpg', '25.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('27', 'Five of Wands', 'There may be obstacles on your path. Courage will be required to overcome them.', '26th.jpg', '26.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('27r', 'Five of Wands Reversed', 'Obstacles to success will be overcome. New ventures are on the horizon.', '26r.jpg', '26.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('28', 'The Six of Wands', 'Good news will come to you. Success will be realized through labor and industry.', '27th.jpg', '27.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('28r', 'The Six of Wands Reversed', 'Success may be delayed. Be wary of accomplished enemies.', '27r.jpg', '27.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('29', 'The Seven of Wands', 'Courage will bring victory. Success seems certain', '28th.jpg', '28.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('29r', 'The Seven of Wands Reversed', 'Employ caution and courage in your ventures. Knowledge is your ally, arrogance your enemy.', '28r.jpg', '28.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('30', 'The Eight of Wands', 'Things done hastily will prove to be poorly done. Think and plan before setting crucial actions into motion.', '29th.jpg', '29.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('30r', 'The Eight of Wands Reversed', 'Delay seems probable. Jealousy may become a problem.', '29r.jpg', '29.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('31', 'The Nine of Wands', 'Your struggles are at a pause. Prepare for them to renew themselves. Persistency will pay off in the end.', '30th.jpg', '30.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('31r', 'Nine of Wands Reversed', 'There are obstacles before you. Health problems, discontent and adversity may need to be overcome.', '30r.jpg', '30.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('32', 'Ten of Wands', 'Strength and power may be misused. Heartache is possible. Persistence will eventually solve all problems. ', '31th.jpg', '31.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('32r', 'Ten Of Wands Reversed', 'Intrigue, separation and loss are possible.', '31r.jpg', '31.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('33', 'Page of Wands', 'This card represents courage and beauty, a quickness to love as well as anger. A carrier of tidings and information.', '32th.jpg', '32.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('33r', 'The Page of Wands Reversed', 'Bad news, cruelty and a lack of stability. ', '32r.jpg', '32.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('34', 'The Knight of Wands', 'Energy and generosity countered by cruelty. Unrestrained impulsiveness. ', '33th.jpg', '33.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('34r', 'The Knight of Wands Reversed', 'Jealousy, conflict and chaos. Work left undone.', '33r.jpg', '33.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('35', 'The Queen of Wands', 'The power to acquire that which is wanted. A productive mind and body. A loving nature. Success.', '34th.jpg', '34.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('35r', 'The Queen of Wands Reversed', 'Vengence and domination, possibly unfaithfulness and deceit.', '34r.jpg', '34.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('36', 'The King of Wands', 'Passionate, a spry body and mind, honesty and friendliness.', '35th.jpg', '35.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('36r', 'The King of Wands Reversed', 'Ruthless, the misuse of power, intolerance and predjudice.', '35r.jpg', '35.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('37', 'Ace of Cups', 'Love, joy, and contentment productiveness and fertility can be had.', '36th.jpg', '36.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('37r', 'Ace of Cups Reversed', 'Love and happiness may be false. Instability and infertility are possibilities.', '36r.jpg', '36.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('38', 'Two of Cups', 'The harmonious blending of the male and female self. All aspects of your life are in balance.', '37th.jpg', '37.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('38r', 'Two of Cups Reversed', 'Folly, misunderstanding and false love are possible. ', '37r.jpg', '37.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('39', 'Three of Cups', 'Good luck and success are possible.  Pleasure is likely.', '38th.jpg', '38.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('39r', 'Three of Cups Reversed', 'Overindulgence leads to pain and failure.', '38r.jpg', '38.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('40', 'Four of Cups', 'A period of rest in your life and dissatisfaction with material gains. Re-evaluation of your goals.', '39th.jpg', '39.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('40r', 'Four of Cups Reversed', 'Awakening from a sedate period. New relationships, goals and ventures are possible.', '39r.jpg', '39.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('41', 'Five of Cups', 'That which was expected to bring joy may bring sorrow. Disillusionment with relationships. The loss of friends, partners and lovers.', '40th.jpg', '40.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('41r', 'Five of Cups Reversed', 'Joy and happiness resurface. New friends and relationships are born. Old friends and loved ones return.', '40r.jpg', '40.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('42', 'Six of Cups', 'Happiness and joy come from the past. New friendships, ventures and knowledge are possible.', '41th.jpg', '41.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('42r', 'Six of Cups Reversed', 'Preoccupation with the past. Overdependence on outdated ideas and morals. ', '41r.jpg', '41.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('43', 'Seven of Cups', 'Deception, an overactive imagination, and the illusion of success. Strengths are not consolidated to work as one.', '42th.jpg', '42.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('43r', 'Seven of Cups Reversed', 'The re-emergence of will and determination. Strengths working in unison. Choices made wisely.', '42r.jpg', '42.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('44', 'Eight of Cups', 'Roaming without a destination. Sadness with relationships. The abandonment of the material world.', '43th.jpg', '43.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('44r', 'Eight of Cups Reversed', 'Interest in the material world and success emerges or returns. Happiness and love are possible. ', '43r.jpg', '43.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('45', 'Nine of Cups', 'Success and happiness are attained. The good things in life can be had. Good health will be enjoyed.', '44th.jpg', '44.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('45r', 'Nine of Cups Reversed', 'Unfulfilled wishes, bad health and deprivation are possible.', '44r.jpg', '44.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('46', 'Ten of Cups', 'Happiness and satisfaction can be had. The spiritual self leads to great loves, friendships and success.', '45th.jpg', '45.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('46r', 'Ten of Cups Reversed', 'Betrayal and failure. The loss of freindship and love. ', '45r.jpg', '45.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('47', 'Page of Cups', 'New business opportunities arise. An overactive imagination. ', '46th.jpg', '46.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('47r', 'Page of Cups Reversed', 'Obstacles and bad news. A lack of creativity. Deception.', '46r.jpg', '46.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('48', 'Knight of Cups', 'Grace mixed with laziness. Preoccupation with sensual fantasies. Look for a message, opportunity or invitation.', '47th.jpg', '47.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('48r', 'Knight of Cups Reversed', 'Scrutinize all ventures and deals carefully. Lies, laziness and underhandedness are possible.', '47r.jpg', '47.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('49', 'Queen of Cups', 'Success and happiness are attainable in all their forms. Visions and dreams are realized. Honesty, devotion and loyalty are enjoyed.', '48th.jpg', '48.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('49r', 'Queen of Cups Reversed', 'An ugly, unreliable character. Dishonesty and immorality are possible.', '48r.jpg', '48.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('50', 'King of Cups', 'Fairness on all levels. Generosity and morality abound. A high degree of skill is displayed in all things attempted.', '49th.jpg', '49.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('50r', 'King of Cups Reversed', 'Misused power and skill. Treachery and the loss of morality and money are possible.', '49r.jpg', '49.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('51', 'Ace of Swords', 'The triumph of power and will. Emotions are felt strongly. Great activity abounds. The heroic personality is near or soon to be born.', '50th.jpg', '50.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('51r', 'Ace of Swords Reversed', 'Pyrrhic victories. Loss and infertility are likley.', '50r.jpg', '50.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('52', 'Two of Swords', 'Relationships are rife with tension. Indeciveness may be a problem. The rhythm of life is well sensed, but without direction.', '51th.jpg', '51.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('52r', 'Two of Swords Reversed', 'Changes in direction are possible, but they may not always be in the right. Release from old bonds. Compassion for the troubled.', '51r.jpg', '51.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('53', 'Three of Swords', 'Upheaval and loss in love and all other types of relationships is possible.', '52th.jpg', '52.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('53r', 'Three of Swords Reversed', 'Confusion and upheaval are possible. ', '52r.jpg', '52.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('54', 'Four of Swords', 'A period of rest and restoration of energies. Tensions subside. Good changes are soon to come.', '53th.jpg', '53.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('54r', 'Four of Swords Reversed', 'Activity begins again. Success will be realized. Move with caution and wisdom as your guides.', '53r.jpg', '53.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('55', 'Five of Swords', 'Failure and defeat are possible. Injustice and cowardliness may appear. ', '54th.jpg', '54.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('55r', 'Five of Swords Reversed', 'Victories may be empty. False pride must be avoided. Sorrow is possible.', '54r.jpg', '54.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('56', 'Six of Swords', 'The future looks brighter. Success will be known.', '55th.jpg', '55.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('56r', 'Six of Swords Reversed', 'Life is at a standstill. Difficulties will probably remain as they are.', '55r.jpg', '55.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('57', 'Seven of Swords', 'Plans may fail. Distrust and dishonesty are possible. Success will not be complete.', '56th.jpg', '56.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('57r', 'Seven of Swords Reversed', 'Good things may come from unforseen sources. Freedom from burdens and the easing of tension are possible.', '56r.jpg', '56.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('58', 'Eight of Swords', 'Indecision and betrayal may imprision you. Fear may be paralyzing and make if difficult to move away from present problems.', '57th.jpg', '57.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('58r', 'Eight of Swords Reversed', 'Fresh starts are possible. ', '57r.jpg', '57.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('59', 'Nine of Swords', 'Sorrow, loss and impossible burdens may be present or near.', '58th.jpg', '58.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('59r', 'Nine of Swords Reversed', 'Time heals all wounds. The future may hold promise unimagined today.', '58r.jpg', '58.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('60', 'Ten of Swords', 'Goals may be unmet. Plans are in jeopardy. Sorrow may be felt deeply. Be kind to those in need.', '59th.jpg', '59.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('60r', 'Ten of Swords Reversed', 'Evil will be defeated. Courage is needed to face challenges. Develop your spiritual self.', '59r.jpg', '59.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('61', 'The Page of Swords', 'Youth if not in age then in spirit. Adept at diplomacy.', '60th.jpg', '60.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('61r', 'Page of Swords Reversed', 'Expect the unexpected. Inane behavior may hamper you. Cunning may be called for.', '60r.jpg', '60.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('62', 'Knight of Swords', 'Youth and bravado. Sometimes a dominating personality, but honest and courageous. Misfortune may be coming or passing.', '61th.jpg', '61.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('62r', 'Knight of Swords Reversed', 'Given to bragging. A bully, prone to fighting. Possibly self destructive.', '61r.jpg', '61.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('63', 'Queen of Swords', 'Often mournful from loss or infertility. Quick witted, gracious and kind but firm.', '62th.jpg', '62.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('63r', 'Queen of Swords Reversed', 'Gossipy, dogmatic and unreliable. Dishonesty may be expected.', '62r.jpg', '62.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('64', 'King of Swords', 'The ability to govern and give good advice. A powerful enemy and staunch friend. Strong, authorative and able to lead.', '63th.jpg', '63.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('64r', 'King of Swords Reversed', 'Distrustful and paranoid. Capable of malice. Power abused.', '63r.jpg', '63.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('65', 'Ace of Pentacles', 'Prosperity, pleasure and beauty can be realized.', '64th.jpg', '64.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('65r', 'Ace of Pentacles Reversed', 'Selfishness, greed and false starts are possible. A life that is unsettled or comfortability that may not be in your best interest.', '64r.jpg', '64.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('66', 'Two of Pentacles', 'At ease with change. Able to juggle more than one situtation at a time. New ventures may be hard to get underway. Hard work mixed with unreliability. ', '65th.jpg', '65.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('66r', 'Two of Pentacles Reversed', 'Faked happiness. Inability to manage more than one thing at a time.', '65r.jpg', '65.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('67', 'Three of Pentacles', 'Material fortunes grow. Crafts or arts are mastered.', '66th.jpg', '66.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('67r', 'Three of Pentacles Reversed', 'Gain is not realized and skills are not mastered.', '66r.jpg', '66.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('68', 'Four of Pentacles', 'Financial gain, success and worldly power seem assured, but lack any higher meaning.', '67th.jpg', '67.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('68r', 'Four of Pentacles Reversed', 'Gain is hindered by obstacles. ', '67r.jpg', '67.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('69', 'Five of Pentacles', 'Poverty and unemployment are possible. Possessions may be lost. A troubled soul is likely.', '68th.jpg', '68.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('69r', 'Five of Pentacles Reversed', 'The reaquisition of worldly possessions after trying times and much labor. Benevolence. The awakening of the spiritual self.', '68r.jpg', '68.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('70', 'Six of Pentacles', 'Material gain, charity and justice are at the forefront. That which is earned will be given.', '69th.jpg', '69.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('70r', 'Six of Pentacles Reversed', 'Gloating of wealth and jealousy. Possesions may be in jeopardy.', '69r.jpg', '69.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('71', 'Seven of Pentacles', 'A lull in the growth of ventures. Speculation may not be wise at this time. Success may not be known.', '70th.jpg', '70.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('71r', 'Seven of Pentacles Reversed', 'After much labor, little gain may be realized.', '70r.jpg', '70.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('72', 'Eight of Pentacles', 'A time to learn the ways of an occupation. A viable venture may be beginning. Handling worldly affairs skillfully.', '71th.jpg', '71.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('72r', 'Eight of Pentacles Reversed', 'Possible failure. Vanity and underhandedness must be watched for. Skills may be misused.', '71r.jpg', '71.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('73', 'Nine of Pentacles', 'Material wealth enjoyed alone. Knowledge of the workings of those things in your best interest. Love of home and garden.', '72th.jpg', '72.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('73r', 'Nine of Pentacles Reversed', 'A time to proceed with caution. Ventures may falter. Properties and friendships may be at risk.', '72r.jpg', '72.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('74', 'Ten of Pentacles', 'Riches either made or inherited may be yours. Attention to family matters may be needed.', '73th.jpg', '73.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('74r', 'Ten of Pentacles Reversed', 'You and your family may suffer through some misfortune. Be wary of high risk ventures.', '73r.jpg', '73.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('75', 'Page of Pentacles', 'Introversion. A healthy admiration for new ideas and scholarly pursuits. Reflective. Good management and organizational skills. A bearer of good tidings.', '74th.jpg', '74.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('75r', 'Page of Pentacles Reversed', 'Loss and overindulgence. Preoccupation with worldly possessions. Bad news may be on the horizon.', '74r.jpg', '74.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('76', 'Knight of Pentacles', 'Responsible, patient, trustworthy and able.', '75th.jpg', '75.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('76r', 'Knight of Pentacles Reversed', 'Stagnation mixed with a dull, timid and indolent nature.', '75r.jpg', '75.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('77', 'Queen of Pentacles', 'Feminine qualities take the forefront. In tune with the earth. Nurturing. Wealth and security. Talents put to good use.', '76th.jpg', '76.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('77r', 'Queen of Pentacles Reversed', 'A lack of trust may prevail. Obligations and duties are not attended. Failure is feared.', '76r.jpg', '76.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('78', 'King of Pentacles', 'A leader in industry and finance. The owner of large properties. Reliable, skilled and successful.', '77th.jpg', '77.jpg');
INSERT INTO `tcards` (`card`, `title`, `description`, `thumb`, `limage`) VALUES ('78r', 'King of Pentacles Reversed', 'Misuse of skills. Prone to vice. Easily corrupted. Possibly dangerous when angered.', '77r.jpg', '77.jpg');
